select 

from log_adminoper
where 1=1 

   and  account = #{account,jdbcType=VARCHAR}


   and  oper = #{oper,jdbcType=VARCHAR}


   and  createtime = #{createtime,jdbcType=TIMESTAMP}

 order by id desc

